import React, { useState } from "react";
import { Formik, Form, Field, useFormik } from "formik";
import * as Yup from "yup";
import { TextField } from "material-ui-formik-components/TextField";
import { Select } from "material-ui-formik-components/Select";
import CircularProgress from "@material-ui/core/CircularProgress";
import { useAuth } from "../contexts/AuthContext";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";
import Button from "@material-ui/core/Button";

function SignUpFormCompanyy() {
  const [loadingCircle, setLoadingCircle] = useState("block");
  const [content, setContent] = useState("none");
  const [city, setCity] = useState([]);
  const [error, setError] = useState();
  const [open, setOpen] = useState(true);
  const [loading, setLoading] = useState(false); //for firebase
  const { signup } = useAuth();

  const sigUpSchema = Yup.object().shape({
    name: Yup.string()
      .min(2, "Company name is too short")
      .required("Company name is required"),
    email: Yup.string()
      .email("Please enter a valid email")
      .required("Email is required"),
    city: Yup.number().required("Please select a city"),
    password: Yup.string().required("Password is required").min(8, "pass 8"),
    confirmPassword: Yup.string().oneOf(
      [Yup.ref("password"), null],
      "Passwords must match"
    ),
  });

  function formatCity(data) {
    var formatted_array = [];
    for (var i = 0; i < data.length; i++) {
      formatted_array.push({
        value: data[i]._id,
        label: data[i].name,
      });
    }
    return formatted_array;
  }

  async function getCity() {
    const response = await fetch("/GetCity");
    const data = await response.json();
    const cities = formatCity(data);
    setCity(cities);
    console.log(city);
    setLoadingCircle("none");
    setContent("block");
  }

  async function postData(company) {
    const response = await fetch("/SignUpCompany", {
      method: "POST",
      headers: {
        "Content-type": "application/json; charset=UTF-8",
      },
      body: JSON.stringify(company),
    });
    console.log(response.status);
    if (response.ok) {
      console.log("okkkk");
    } else {
      console.log("nöööö");
    }
  }

  async function handleSubmit(values) {
    setLoading(true);
    postData(values);
    try {
      setError("");
      await signup(values.email, values.password);
    } catch {
      console.log("catch");
      setOpen(true);
      setError("Failed to create an account on firebase");
    }
    setOpen(true);
    setLoading(false);
  }

  if (city.length < 1) {
    getCity();
  }
  return (
    <div>
      <div style={{ display: loadingCircle }}>
        <CircularProgress />
      </div>
      <div>
        <Dialog
          open={open}
          onClose={setOpen(false)}
          aria-labelledby="alert-dialog-title"
          aria-describedby="alert-dialog-description"
        >
          <DialogTitle id="customized-dialog-title" onClose={setOpen(false)}>
            <IconButton aria-label="close" onClick={setOpen(false)}>
              <CloseIcon />
            </IconButton>
          </DialogTitle>

          <DialogContent>
            <DialogContentText id="alert-dialog-description">
              {error}
            </DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button onClick={setOpen(false)} color="primary" autoFocus>
              OK
            </Button>
          </DialogActions>
        </Dialog>
      </div>
      <div style={{ display: content }}>
        <Formik
          initialValues={{
            name: "",
            email: "",
            city: "",
            password: "",
            confirmPassword: "",
          }}
          onSubmit={handleSubmit}
          validationSchema={sigUpSchema}
        >
          {(formik) => (
            <Form>
              <Field name="name" label="Name" component={TextField} />
              <Field
                name="email"
                label="Email"
                type="email"
                component={TextField}
              />
              <Field
                required
                fullWidth
                name="city"
                label="City"
                options={city}
                component={Select}
              />
              <Field
                required
                fullWidth
                name="password"
                label="Password"
                type="password"
                component={TextField}
              />
              <Field
                required
                fullWidth
                name="confirmPassword"
                label="Confirm Password"
                type="password"
                component={TextField}
              />
              <button type="submit" disabled={!formik.dirty || !formik.isValid}>
                Submit
              </button>
            </Form>
          )}
        </Formik>
      </div>
    </div>
  );
}

export default SignUpFormCompanyy;
