import React, { useState } from "react";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";

import TextField from "@material-ui/core/TextField";

function SignUpFormCompanyy() {
  const [showPassword, setShowPassword] = useState(false);

  const handleChange = (prop) => (event) => {
    setValues({ ...values, [prop]: event.target.value });
  };

  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };
  const initialValues = {
    name: "",
    email: "",
    city: "",
    password: "",
    confirmPassword: "",
  };
  function handleSubmit(values) {
    console.log(values);
  }
  const sigUpSchema = Yup.object().shape({
    name: Yup.string()
      .min(2, "Company name is too short")
      .required("Company name is required"),
    city: Yup.string().required("Please select a city"),
  });
  return (
    <div>
      <h1>Sign Up</h1>
      <Formik
        initialValues={initialValues}
        onSubmit={handleSubmit}
        validationSchema={sigUpSchema}
      >
        {({ dirty, isValid }) => {
          return (
            <Form>
              <div>
                <Field
                  name="name"
                  as="input"
                  placeholder="Company Name"
                ></Field>
                <ErrorMessage name="name" />
              </div>
              <div>
                <TextField required id="standard-basic" label="Name" />
              </div>
              <div>
                <FormControl
                  className={clsx(classes.margin, classes.textField)}
                >
                  <InputLabel htmlFor="standard-adornment-password">
                    Password
                  </InputLabel>
                  <Input
                    id="standard-adornment-password"
                    type={showPassword ? "text" : "password"}
                    value={values.password}
                    onChange={handleChange("password")}
                    endAdornment={
                      <InputAdornment position="end">
                        <IconButton
                          aria-label="toggle password visibility"
                          onClick={setShowPassword(true)}
                          onMouseDown={handleMouseDownPassword}
                        >
                          {showPassword ? <Visibility /> : <VisibilityOff />}
                        </IconButton>
                      </InputAdornment>
                    }
                  />
                </FormControl>
              </div>
              <div>
                <Field name="city" as="select" placeholder="City">
                  <option value="empty">Select</option>
                  <option value="ist">İst</option>
                </Field>
                <ErrorMessage name="city" />
              </div>
              <button disabled={!dirty || !isValid} type="submit">
                Submit
              </button>
            </Form>
          );
        }}
      </Formik>
    </div>
  );
}

export default SignUpFormCompanyy;
