import React, { useState } from "react";
import { Formik, Form, Field } from "formik";
import * as Yup from "yup";
import { TextField } from "material-ui-formik-components/TextField";
import { resetPassword } from "../contexts/AuthContext";
import Button from "@material-ui/core/Button";
import { useHistory, Link } from "react-router-dom";

function ForgotPassword() {
  const [error, setError] = useState();
  const [loading, setLoading] = useState(false); //for firebase
  const history = useHistory();
  const signUpSchema = Yup.object().shape({
    email: Yup.string()
      .email("Please enter a valid email")
      .required("Email is required"),
  });

  //TODO: If one of the sign up attempts fails the other one sould be deleted
  async function handleSubmit(values) {
    try {
      setError("");
      setLoading(true);
      await resetPassword(values.email);
    } catch {
      console.log("catch");
      setError("Failed to create an account on firebase");
    }

    setLoading(false);
  }

  return (
    <div>
      <div>
        <Formik
          initialValues={{
            email: "",
          }}
          onSubmit={handleSubmit}
          validationSchema={signUpSchema}
        >
          {(formik) => (
            <Form>
              <Field
                required
                name="email"
                label="Email"
                type="email"
                component={TextField}
              />

              <Button
                variant="contained"
                color="primary"
                type="submit"
                disabled={!formik.dirty || !formik.isValid || loading}
              >
                Rset Password
              </Button>
            </Form>
          )}
        </Formik>
      </div>
    </div>
  );
}

export default ForgotPassword;
