import React, { useState } from "react";
import { Form } from "semantic-ui-react";
import { useAuth } from "../contexts/AuthContext";
import Message from "./Message";
import { Link } from "react-router-dom";
import { useFormik, Formik } from "formik";

function SignUpFormCompany() {
  const [city, setCity] = useState(null);
  const [loaded, setLoaded] = useState(false); //for form select values
  const [error, setError] = useState();
  const [loading, setLoading] = useState(false); //for firebase
  const { signup } = useAuth();
  const formik = useFormik({
    initialValues: {
      companyName: "",
      email: "",
      companyCity: "",
      password: "",
      confirmPassword: "",
    },
    onSubmit: (values) => {
      console.log(values);
    },
  });

  //TODO: Check for if the email has already been used

  function formatCity(data) {
    var formatted_array = [];
    for (var i = 0; i < data.length; i++) {
      formatted_array.push({
        key: data[i]._id,
        text: data[i].name,
        value: data[i]._id,
      });
    }
    return formatted_array;
  }

  async function getCity() {
    const response = await fetch("/GetCity");
    const data = await response.json();
    const cities = formatCity(data);

    setCity(cities);
    setLoaded(true);
  }

  async function handleSubmit(e) {
    e.preventDefault();
    formik.handleSubmit();
  }

  if (city == null) {
    getCity();
  }
  if (loaded == true) {
    const element = document.getElementById("form");
    element.classList.remove("loading");
  }

  return (
    <>
      <Formik>
        <Form id="form" onSubmit={(e) => handleSubmit(e)} loading>
          {error && <Message message={error} />}
          <div>
            <Form.Input
              id="companyNameInput"
              fluid
              name="companyName"
              label="Company Name"
              placeholder="Name"
              value={formik.values.companyName}
              onChange={formik.handleChange}
            />
          </div>
          <br />
          <div>
            <Form.Input
              id="companyEmailInput"
              fluid
              type="email"
              name="email"
              label="Company Email"
              placeholder="Email"
              value={formik.values.email}
              onChange={formik.handleChange}
            />
          </div>
          <br />
          <div>
            <Form.Select
              fluid
              selection
              label="City"
              name="companyCity"
              options={city}
              placeholder="City"
              value={formik.values.companyCity}
              onChange={formik.handleChange}
            />
          </div>
          <br />
          <div>
            <Form.Input
              id="companyPassInput"
              fluid
              label="Enter Password"
              name="password"
              type="password"
              value={formik.values.password}
              onChange={formik.handleChange}
            />
          </div>
          <br />
          <div>
            <Form.Input
              id="companyConfirmPassInput"
              fluid
              name="confirmPassword"
              label="Confirm Password"
              type="password"
              value={formik.values.confirmPassword}
              onChange={formik.handleChange}
            />
          </div>
          <br />
          <Form.Button id="submit" disabled={loading}>
            Sign Up
          </Form.Button>
        </Form>
        <br />
        <div>
          You already have an account?
          <Link value="signin" to="/Home/SignIn">
            Sign In
          </Link>
        </div>
      </Formik>
    </>
  );
}

export default SignUpFormCompany;
