import React, {useState} from 'react'
import { Form } from 'semantic-ui-react'

function SignUpFormCompany(){

  const [company_name, setCompanyName] = useState("")
  const [email, setEmail] = useState("")
  const [pass, setPass] = useState("")
  const [confirm_pass, setConfirmPass] = useState("")
  const [error,setError] = useState(
    {
      "company_name": "",
      "email": "",
      "password": [],
      "confirm_password": ""
    }
  )



  function isValid(){
    var format = /[ `!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~]/;
    function hasUpper(str){
      return (/[A-Z]/.test(str))
    }
    if(!company_name.trim()){
      setError(prevState => prevState = "Company name is required")
    }
    if(!email.trim()){
      setError(prevState => prevState = "Email required") 
    }else if(!/^[A-Z0-9._%+-]+@[[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(email)){
      setError(prevState => prevState = "Email is invalid");
    }
    if(!pass){
      setError(prevState => prevState.push("Password is required"));
    }if(pass.length < 8){
      setError(prevState => prevState.push("Password needs to be at least 8 characters"));
    }if(!format.test(pass)){
      setError(prevState => prevState.push("Passwords must contain at least one special character"));
    }if(!hasUpper(pass)){
      setError(prevState => prevState.push("Password must contain at leat one upper case character"));
    }
    if(!confirm_pass){
      setError(prevState => prevState = "This field is reuired") ;
    }else if(pass !== confirm_pass){
      setError(prevState => prevState = "Passwords must match");
    }
  }

  function updateState(func,e){
    func(prevState => prevState = e.target.value)
  }

  function handleSubmit(e){
    e.preventDefault();
    isValid();
    console.log(error.password)
  }


  return (
    <div>
    <Form onSubmit = {e => handleSubmit(e)}>
      <div>
        <Form.Input fluid id="company_name" label='Company Name' placeholder='Name' value={company_name} onChange={e => updateState(setCompanyName,e)}/>
        
        <p>{error.company_name}</p>
      </div>
      <Form.Input fluid id="email" type="email" label='Company Email' placeholder='Email' value={email} onChange={e => updateState(setEmail,e)}/>
      <Form.Input fluid id="pass" label='Enter Password' type='password' value={pass} onChange={e => updateState(setPass,e)}/>
      <Form.Input fluid id="confirm_pass" label='Confirm Password' type='password' value={confirm_pass} onChange={e => updateState(setConfirmPass,e)}/>
      <Form.Button>Sign Up</Form.Button>
    </Form>
    </div>
  );
}

export default SignUpFormCompany;
