import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import { Segment, Form, Modal, Button } from "semantic-ui-react";
import { useAuth } from "../contexts/AuthContext";
function exampleReducer(state, action) {
  switch (action.type) {
    case "OPEN_MODAL":
      return { open: true, dimmer: action.dimmer };
    case "CLOSE_MODAL":
      return { open: false };
    default:
      throw new Error();
  }
}
export default function ResetPassword() {
  const [email, setEmail] = useState();
  const { resetPassword } = useAuth();
  const [modalOpen, setModalOpen] = useState(false);
  const history = useHistory();

  async function handleSubmit(e) {
    e.preventDefault();
    try {
      await resetPassword(email);
      setModalOpen(true);
    } catch {}
  }
  const [state, dispatch] = React.useReducer(exampleReducer, {
    open: false,
    dimmer: undefined,
  });
  const { open, dimmer } = state;

  return (
    <div>
      <Segment raised>
        <Form id="form" onSubmit={(e) => handleSubmit(e)}>
          <div>
            <Form.Input
              id="emailInput"
              fluid
              type="email"
              label="Email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <br />
          <Form.Button id="submit">Reset Password</Form.Button>
        </Form>
      </Segment>

      <div>
        <Modal
          dimmer={dimmer}
          open={modalOpen}
          onClose={() => dispatch({ type: "CLOSE_MODAL" })}
        >
          <Modal.Header>Email sent to your account.</Modal.Header>
          <Modal.Content>Please check your mail.</Modal.Content>
          <Modal.Actions>
            <Button
              positive
              onClick={() => {
                dispatch({ type: "CLOSE_MODAL" });
                history.push("/Home");
              }}
            >
              Got it!
            </Button>
          </Modal.Actions>
        </Modal>
      </div>
    </div>
  );
}
