import React, { useState } from "react";
import { Form } from "semantic-ui-react";
import $ from "jquery";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect,
} from "react-router-dom";
import HomePageStudent from "../Pages/HomePageStudent";
import NotFound from "../Pages/404Page";

function SignUpFormCompany(props) {
  const [email, setEmail] = useState(null);
  const [pass, setPass] = useState(null);
  const [email_error, setEmailError] = useState(null);
  const [pass_error, setPassError] = useState([]);

  //TODO: Add proper validation
  function isValid() {
    var format = /[ `!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~]/;
    function hasUpper(str) {
      return /[A-Z]/.test(str);
    }
    if (!email.trim()) {
      setEmailError((prevState) => (prevState = "Please enter email"));
    }
  }

  function updateState(func, e) {
    func((prevState) => (prevState = e.target.value));
  }

  //FIXME: Con't show the error messages
  function handleError() {
    $(function () {
      $("#submit").on("click", function () {
        $("#emailInput").attr("error", { content: email_error });
        $("#passInput").attr("error", { content: pass_error });
      });
    });
  }

  async function postData() {
    var address = "";
    const user = { email: email, password: pass };
    console.log(props.user);
    if (props.user === "student") {
      address = "/SignInStudent";
    } else if (props.user === "company") {
      address = "/SignInCompany";
    }
    const response = await fetch(address, {
      method: "POST",
      headers: {
        "Content-type": "application/json; charset=UTF-8",
      },
      body: JSON.stringify(user),
    });

    if (response.ok) {
      console.log("okkkk");
    } else {
      console.log("nöööö");
    }
  }

  function hasError() {
    if (email_error === null || pass_error === null) {
      return false;
    } else {
      return true;
    }
  }

  function redirect() {
    return (
      <Router>
        <Switch>
          <Route exact path="/StudentHome" compnent={HomePageStudent}></Route>
          <Route exact path="/404NotFound" component={NotFound} />
          <Redirect to="/404NotFound" />
        </Switch>
      </Router>
    );
  }

  async function handleSubmit(e) {
    e.preventDefault();
    isValid();
    if (!hasError()) {
      postData();
      redirect();
    } else {
      handleError();
    }
    //handleError();
  }

  return (
    <div>
      <Form id="form" onSubmit={(e) => handleSubmit(e)}>
        <div>
          <Form.Input
            id="emailInput"
            fluid
            type="email"
            label="Email"
            placeholder="Email"
            value={email}
            onChange={(e) => updateState(setEmail, e)}
          />
        </div>
        <div>
          <Form.Input
            id="passInput"
            fluid
            label="Enter Password"
            type="password"
            value={pass}
            onChange={(e) => updateState(setPass, e)}
          />
        </div>
        <Form.Button id="submit">Sign In</Form.Button>
      </Form>
    </div>
  );
}

export default SignUpFormCompany;
