import React, {useState} from 'react'
import { Form } from 'semantic-ui-react'

function SignUpFormCompany(){

  const [company_name, setCompanyName] = useState("")
  const [email, setEmail] = useState("")
  const [pass, setPass] = useState("")
  const [confirm_pass, setConfirmPass] = useState("")

  // function isValid(e){

  // }

  function updateState(func,e){
    func(prevState => prevState = e.target.value)
  }

  function handleSubmit(e){
    e.preventDefault();
    console.log(company_name)
  }


  return (
    <Form onSubmit = {e => handleSubmit(e)}>
      <Form.Input fluid id="company_name" label='Company Name' placeholder='Name' value={company_name} onChange={e => updateState(setCompanyName,e)}/>
      <Form.Input fluid id="email" type="email" label='Company Email' placeholder='Email' value={email} onChange={e => updateState(setEmail,e)}/>
      <Form.Input fluid id="pass" label='Enter Password' type='password' value={pass} onChange={e => updateState(setPass,e)}/>
      <Form.Input fluid id="confirm_pass" label='Confirm Password' type='password' value={confirm_pass} onChange={e => updateState(setConfirmPass,e)}/>
      <Form.Button>Sign Up</Form.Button>
    </Form>
  );
}

export default SignUpFormCompany;
