import React, { Fragment, useState } from "react";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import TextField from "@material-ui/core/TextField";
import Divider from "@material-ui/core/Divider";
import Phone from "material-ui-phone-number";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import Input from "@material-ui/core/Input";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogTitle from "@material-ui/core/DialogTitle";

export default function PrepareCV() {
  const [phone, setPhone] = useState();
  const [title, setTitle] = useState();
  const [open, setOpen] = React.useState(false);
  const [birthday, setBirthday] = useState({
    day: "",
    month: "",
    year: "",
  });
  const [birthdayButton, setBirthdayButton] = useState(
    "Click to select date of birth"
  );
  const [address, setAddress] = useState({
    city: "",
    district: "",
  });
  const [cities, setCities] = useState([]);
  const [districts, setDistricts] = useState();
  const days = Array(31)
    .fill()
    .map((_, i) => i + 1);
  const months = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];
  const years = Array(101)
    .fill()
    .map((_, i) => i + 1920);

  function formatCity(data) {
    var formatted_array = [];
    for (var i = 0; i < data.length; i++) {
      formatted_array.push({
        value: data[i]._id,
        label: data[i].name,
      });
    }
    return formatted_array;
  }

  async function getCity() {
    const response = await fetch("/GetCity");
    const data = await response.json();
    const cities = formatCity(data);
    setCities(cities);
  }

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };
  const handleOK = () => {
    setOpen(false);
    var b_string =
      birthday.day.toString() +
      " " +
      birthday.month +
      " " +
      birthday.year.toString();
    console.log(b_string);
    setBirthdayButton(b_string);
  };
  if (cities == []) {
    getCity();
  }
  return (
    <div>
      <Paper elevation={2}>
        <Typography variant="h5" component="h2">
          Personal Information
        </Typography>
        <Divider variant="middle" />
        <form>
          <TextField
            value={title}
            label="Title"
            onChange={(title) => setTitle(title)}
          ></TextField>
          <Phone
            label="Phone Number"
            defaultCountry={"tr"}
            onChange={(phone) => setPhone(phone)}
          />
          <div>
            <Button onClick={handleClickOpen}>{birthdayButton}</Button>
            <Dialog
              disableBackdropClick
              disableEscapeKeyDown
              fullWidth={true}
              maxWidth="xs"
              open={open}
              onClose={handleClose}
            >
              <DialogTitle>Choose Birthday</DialogTitle>
              <DialogContent>
                <form>
                  <FormControl style={{ minWidth: 100, margin: "0 2% 0 2%" }}>
                    <InputLabel htmlFor="demo-dialog-native">Day</InputLabel>
                    <Select
                      value={birthday.day}
                      onChange={(e) =>
                        setBirthday({
                          ...birthday,
                          day: e.target.value,
                        })
                      }
                      input={<Input id="demo-dialog-native" />}
                    >
                      {days.map((d) => {
                        return <MenuItem value={d}>{d}</MenuItem>;
                      })}
                    </Select>
                  </FormControl>
                  <FormControl style={{ minWidth: 100, margin: "0 2% 0 2%" }}>
                    <InputLabel id="demo-dialog-select-label">Month</InputLabel>
                    <Select
                      labelId="demo-dialog-select-label"
                      id="demo-dialog-select"
                      value={birthday.month}
                      onChange={(e) =>
                        setBirthday({
                          ...birthday,
                          month: e.target.value,
                        })
                      }
                      input={<Input />}
                    >
                      {months.map((m) => {
                        return <MenuItem value={m}>{m}</MenuItem>;
                      })}
                    </Select>
                  </FormControl>
                  <FormControl style={{ minWidth: 100, margin: "0 2% 0 2%" }}>
                    <InputLabel id="demo-dialog-select-label">Year</InputLabel>
                    <Select
                      labelId="demo-dialog-select-label"
                      id="demo-dialog-select"
                      value={birthday.year}
                      onChange={(e) =>
                        setBirthday({
                          ...birthday,
                          year: e.target.value,
                        })
                      }
                      input={<Input />}
                    >
                      {years.map((y) => {
                        return <MenuItem value={y}>{y}</MenuItem>;
                      })}
                    </Select>
                  </FormControl>
                </form>
              </DialogContent>
              <DialogActions>
                <Button onClick={handleClose} color="primary">
                  Cancel
                </Button>
                <Button onClick={handleOK} color="primary">
                  Ok
                </Button>
              </DialogActions>
            </Dialog>
          </div>
          <div>
            <form>
              <FormControl style={{ minWidth: 100, margin: "0 2% 0 2%" }}>
                <InputLabel id="demo-dialog-select-label">City</InputLabel>
                <Select
                  labelId="demo-dialog-select-label"
                  id="demo-dialog-select"
                  value={address.city}
                  onChange={(e) =>
                    setAddress({
                      ...address,
                      city: e.target.value,
                    })
                  }
                  input={<Input />}
                >
                  {cities.map((y) => {
                    return <MenuItem value={y.value}>{y.label}</MenuItem>;
                  })}
                </Select>
              </FormControl>
              <FormControl style={{ minWidth: 100, margin: "0 2% 0 2%" }}>
                <InputLabel id="demo-dialog-select-label">Year</InputLabel>
                <Select
                  labelId="demo-dialog-select-label"
                  id="demo-dialog-select"
                  value={birthday.year}
                  onChange={(e) =>
                    setBirthday({
                      ...birthday,
                      year: e.target.value,
                    })
                  }
                  input={<Input />}
                >
                  {years.map((y) => {
                    return <MenuItem value={y}>{y}</MenuItem>;
                  })}
                </Select>
              </FormControl>
            </form>
          </div>
        </form>
      </Paper>
    </div>
  );
}
