import React, { useState } from "react";
import { Formik, Form, Field, useFormik } from "formik";
import * as Yup from "yup";
import { TextField } from "material-ui-formik-components/TextField";
import { Select } from "material-ui-formik-components/Select";
import CircularProgress from "@material-ui/core/CircularProgress";

function SignUpFormCompanyy() {
  const mockitems = [
    { label: "ist", value: 34 },
    { label: "ankara", value: 6 },
  ];
  const [city, setCity] = useState();
  const [loaded, setLoaded] = useState(false);
  const yup = require("yup");
  require("yup-password")(yup);

  const sigUpSchema = Yup.object().shape({
    name: Yup.string()
      .min(2, "Company name is too short")
      .required("Company name is required"),
    email: Yup.string()
      .email("Please enter a valid email")
      .required("Email is required"),
    city: Yup.number().required("Please select a city"),

    password: Yup.string()
      .password()
      .required("Password is required")
      .min(8, "pass 8")
      .minLowercase(1, "has min low")
      .minUppercase(1, "has min high")
      .minNumber(1, "has min num")
      .minSymbol(1, "has min sym"),
    confirmPassword: Yup.string().oneOf(
      [Yup.ref("password"), null],
      "Passwords must match"
    ),
  });

  async function getCity() {
    const response = await fetch("/GetCity");
    const data = await response.json();
    const cities = formatCity(data);

    setCity(cities);
    setLoaded(true);
  }

  return (
    <div>
      <div display={!loaded}>
        <CircularProgress />
      </div>
      <div display={loaded}>
        <Formik
          initialValues={{
            name: "",
            email: "",
            city: "",
            password: "",
            confirmPassword: "",
          }}
          onSubmit={(values) => {
            alert(`Name: ${values.name}\nCity: ${values.city}`);
          }}
          validationSchema={sigUpSchema}
        >
          {(formik) => (
            <Form>
              <Field name="name" label="Name" component={TextField} />
              <Field
                name="email"
                label="Email"
                type="email"
                component={TextField}
              />
              <Field
                required
                fullWidth
                name="city"
                label="City"
                options={city}
                component={Select}
              />
              <Field
                required
                fullWidth
                name="password"
                label="Password"
                type="password"
                component={TextField}
              />
              <Field
                required
                fullWidth
                name="confirmPassword"
                label="Confirm Password"
                type="password"
                component={TextField}
              />
              <button type="submit" disabled={!formik.dirty || !formik.isValid}>
                Submit
              </button>
            </Form>
          )}
        </Formik>
      </div>
    </div>
  );
}

export default SignUpFormCompanyy;
