import React, {useState} from 'react'
import { Form } from 'semantic-ui-react'

function SignUpFormCompany(){

  const [company_name, setCompanyName] = useState("")
  const [email, setEmail] = useState("")
  const [pass, setPass] = useState("")
  const [confirm_pass, setConfirmPass] = useState("")
  const [company_name_error,setCompanyNameError] =useState("")
  const [email_error,setEmailError] =useState("")
  const [pass_error,setPassError] = useState({err:[]})
  const [confirm_pass_error,setConfirmPassError] = useState("")

  function isValid(){
    var format = /[ `!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~]/;
    function hasUpper(str){
      return (/[A-Z]/.test(str))
    }
    if(!company_name.trim()){
      setCompanyNameError(prevState => prevState = "Company name is required")
    }
    if(!email.trim()){
      setEmailError(prevState => prevState = "Email required") 
    }else if(!/^[A-Z0-9._%+-]+@[[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(email)){
      setEmailError(prevState => prevState = "Email is invalid");
    }
    // if(!pass){
    //   setPassError(prevState => prevState.push("Password is required"));
    // }if(pass.length < 8){
    //   setPassError(prevState => prevState.push("Password needs to be at least 8 characters"));
    // }if(!format.test(pass)){
    //   setPassError(prevState => prevState.push("Passwords must contain at least one special character"));
    // }if(!hasUpper(pass)){
    //   setPassError(prevState => prevState.push("Password must contain at leat one upper case character"));
    // }
    if(!pass){
      setPassError(prevState => ({
        err : [...prevState.err,"Password is required" ]
      }));
    }if(pass.length < 8){
      setPassError(prevState => ({
        err : [...prevState.err,"Password needs to be at least 8 characters" ]
      }));
    }if(!format.test(pass)){
      setPassError(prevState => ({
        err : [...prevState.err,"Passwords must contain at least one special character" ]
      }));
    }if(!hasUpper(pass)){
      setPassError(prevState => ({
        err : [...prevState.err,"Password must contain at leat one upper case character" ]
      }));
    }
    if(!confirm_pass){
      setConfirmPassError(prevState => prevState = "This field is reuired") ;
    }else if(pass !== confirm_pass){
      setConfirmPassError(prevState => prevState = "Passwords must match");
    }
  }

  function updateState(func,e){
    func(prevState => prevState = e.target.value)
  }

  function handleSubmit(e){
    e.preventDefault();
    isValid();
  }

  function printPassError(){
    let data = ""
    for(let i = 0; i < pass_error.err;i++){
      data += "<p>{pass_error.err[i]}</p>"
      console.log(i)
    }
  }

  return (
    <div>
    <Form onSubmit = {e => handleSubmit(e)}>
      <div>
        <Form.Input fluid id="company_name" label='Company Name' placeholder='Name' value={company_name} onChange={e => updateState(setCompanyName,e)}/>
        <p>{company_name_error}</p>
      </div>
      <div>
        <Form.Input fluid id="email" type="email" label='Company Email' placeholder='Email' value={email} onChange={e => updateState(setEmail,e)}/>
        <p>{email_error}</p>
      </div>
      <div>
        <Form.Input fluid id="pass" label='Enter Password' type='password' value={pass} onChange={e => updateState(setPass,e)}/>
        <p>{printPassError}</p>
        
      </div>
      <div>
        <Form.Input fluid id="confirm_pass" label='Confirm Password' type='password' value={confirm_pass} onChange={e => updateState(setConfirmPass,e)}/>
        <p>{confirm_pass_error()}</p>
      </div>
      <Form.Button>Sign Up</Form.Button>
    </Form>
    </div>
  );
}

export default SignUpFormCompany;
