import React, { useState } from "react";
import { Formik, Form, Field, useFormik } from "formik";
import * as Yup from "yup";
import { TextField } from "material-ui-formik-components/TextField";
import { Select } from "material-ui-formik-components/Select";
import Password from "./FormikField/Password";
function SignUpFormCompanyy() {
  const mockitems = [
    { label: "ist", value: 34 },
    { label: "ankara", value: 6 },
  ];
  const sigUpSchema = Yup.object().shape({
    name: Yup.string()
      .min(2, "Company name is too short")
      .required("Company name is required"),
    email: Yup.string()
      .email("Please enter a valid email")
      .required("Email is required"),
    city: Yup.number().required("Please select a city"),

    password: Yup.string()
      .required("Password is required")
      .min(8, "Password must contain at least 8 characters")
      .matches(
        /^((?=.*[A-Z]){1,}).*$/,
        "Password must contain at least one uppercase character"
      )
      .matches(
        /^((?=.*[!@#$%^&*()\-_=+{};:,<.>]){1,}).*$/,
        "Password must contain at least one special character"
      ),
    confirmPassword: Yup.string().oneOf(
      [Yup.ref("password"), null],
      "Passwords must match"
    ),
  });

  return (
    <Formik
      initialValues={{
        name: "",
        email: "",
        city: "",
        password: "",
        confirmPassword: "",
      }}
      onSubmit={(values) => {
        alert(`Name: ${values.name}\nCity: ${values.password}`);
      }}
      validationSchema={sigUpSchema}
    >
      {(formik) => (
        <Form>
          <Field name="name" label="Name" component={TextField} />
          <Field
            name="email"
            label="Email"
            type="email"
            component={TextField}
          />
          <Field
            required
            fullWidth
            name="city"
            label="City"
            options={mockitems}
            component={Select}
          />
          <Password name="password" label="Password"></Password>
          <Password name="confirmPassword" label="Confirm Password"></Password>
          <button type="submit" disabled={!formik.dirty || !formik.isValid}>
            Submit
          </button>
        </Form>
      )}
    </Formik>
  );
}

export default SignUpFormCompanyy;
